import React from "react";
import "./Input.css";

// Vamos a partir de una web sencilla con un input de tipo texto y un párrafo vacío. ¿Seremos capaces de hacer que con React y el estado, cuando modificamos el input aparezca el texto en el párrafo?

class Input extends React.Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
    this.state = {
      text: "He conseguido hacer funcionar el state",
    };
  }
  handleClick(ev) {
    const input = ev.target.value;

    if (input === "cebolla") {
      alert("hola");
    }
  }

  render() {
    return (
      <section>
        <input type="text" onChange={this.handleClick} className="input" />
      </section>
    );
  }
}

Input.propTypes = {};
export default Input;
