import React from "react";
import "./Input.css";

// Vamos a partir de una web sencilla con un input de tipo texto y un párrafo vacío. ¿Seremos capaces de hacer que con React y el estado, cuando modificamos el input aparezca el texto en el párrafo?

class Input extends React.Component {
  constructor(props) {
    super(props);
    this.handle = {};
  }
  handleClick(ev) {
    const input = ev.target.value;
    console.log(input);

    if (input.value === "cebolla") {
      alert("hola");
    }
  }

  render() {
    return (
      <section>
        <input type="text" className="input" />
        <p className="paragraph" onChange={this.handleClick}></p>
      </section>
    );
  }
}

Input.propTypes = {};
export default Input;
