import React from "react";

// Vamos a partir de una web sencilla con un input de tipo texto y un párrafo vacío. ¿Seremos capaces de hacer que con React y el estado, cuando modificamos el input aparezca el texto en el párrafo?

class Input extends React.Component {
  //   constructor(props) {
  //     super(props);
  //     this.state = {};
  //   }
  handleClick(ev) {
    const input = ev.target.value;
    console.log(input);
    // const p = document.querySelector(".parrafo");
    if (input.value === "cebolla") {
      alert("hola");
    }
  }

  render() {
    return (
      <section>
        <p className="parrafo" onChange={this.handleClick}></p>
      </section>
    );
  }
}

Input.propTypes = {};
export default Input;
