import React from "react";
import "./Div.css";

//función click

class Div extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      peace: "square",
      angry: "red",
    };
    this.handleClick = this.handleClick.bind(this);
  }
  handleClick(ev) {
    if (this.state.peace === "square") {
      this.setState = { angry };
    }
  }
  render() {
    return <div className="square" onClick={this.handleClick}></div>;
  }
}

export default Div;
