import React from "react";
import "./Div.css";

class DIV extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      div: "blue",
    };
    this.handleClick = this.hancleClick.bind(this);
  }
  hancleClick() {
    if (this.state.div === "blue") {
      this.setState({
        div: "red",
      });
    }
  }
  render() {
    return <div onClick={this.handleClick}></div>;
  }
}

export default DIV;
