import React from "react";
import "./Div.css";

class Div extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      div: "blue",
    };
    this.state.handleClick = this.state.hancleClick.bind(this);
  }
  handleClick() {
    this.setState({
      div: "red",
    });
  }
  render() {
    return <div className={this.state.div} onClick={this.handleClick}></div>;
  }
}

export default Div;
