import React from "react";
import "./Div.css";

class DIV extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      div: "square",
      div2: "red",
    };
    this.handleClick = this.hancleClick.bind(this);
  }
  hancleClick() {
    this.setState({
      div: "red",
    });
  }
  render() {
    return <div className="div red" onClick={this.handleClick}></div>;
  }
}

export default DIV;
