import React from "react";
import "./Clock.css";

class Clock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      clockShape: "clock",
      hour: "Aquí va la hora",
    };
    this.updateClock = this.updateClock.bind(this);
    setInterval(this.updateClock, 1000);
  }
  updateClock() {
    let date = new Date("March 13, 08 04:20");
    this.setState = {
      hour: date.getHours(),
    };
  }

  render() {
    return (
      <div className={this.state.clockShape}>The hour is{this.state.hour}</div>
    );
  }
}

Clock.propTypes = {};
export default Clock;
