import React from "react";
import "./Clock.css";

class Clock extends React.Component {
  constructor(props) {
    super(props);
    setInterval(this.updateClock, 1000);
    this.state = {
      clock: "clock",
    };
    this.updateClock = this.updateClock.bind(this);
  }
  updateClock() {
    const clockDay = new Date("November 09, 2020 23:15:30");
    const day1 = clockDay.getDay();
  }

  render() {
    return <div className={this.state.clock}>Holi{this.updateClock}</div>;
  }
}

Clock.propTypes = {};
export default Clock;
