import React from "react";
import "./Clock.css";

class Clock extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      clockShape: "clock",
      hour: 0,
    };
    this.updateClock = this.updateClock.bind(this);
    setInterval(this.updateClock, 1000);
  }
  updateClock() {
    const clockDay = new Date("November 09, 2020 23:15:30");

    this.setState = {
      clockText: clockDay.getDate(),
    };
    console.log(this.setState);
  }

  render() {
    return (
      <div className={this.state.clockShape}>The hour is{this.updateClock}</div>
    );
  }
}

Clock.propTypes = {};
export default Clock;
