import React from "react";
import "./Clock.css";

class Clock extends React.Component {
  constructor(props) {
    super(props);
    setInterval(this.updateClock, 1000);
    this.state = {
      clockShape: "clock",
      clockText: "17:46:30",
    };
    this.updateClock = this.updateClock.bind(this);
  }
  updateClock() {
    const clockDay = new Date("November 09, 2020 23:15:30");
    this.setState = {
      clockText: clockDay.getHours(),
    };
  }

  render() {
    return (
      <div className={this.state.clockShape} onClick={this.updateClock}>
        Today day is{this.state.clockText}
      </div>
    );
  }
}

Clock.propTypes = {};
export default Clock;
