import React from "react";
import "./Clock.css";

class Clock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      clockShape: "clock",
      hour: "Aquí va la hora",
      minutes: "Minutos",
      seconds: 0,
    };
    this.updateClock = this.updateClock.bind(this);
    setInterval(this.updateClock, 1000);
  }
  updateClock() {
    let date = new Date("March 13, 08 04:20");
    this.setState = {
      hour: date.getHours(),
      minutes: 0,
    };
  }

  render() {
    return (
      <div className={this.state.clockShape}>
        <p>The hour is{this.state.hour}</p>
      </div>
    );
  }
}

Clock.propTypes = {};
export default Clock;
